package calculation;

public class Sample01 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 5,b = 3;
        System.out.println("a = " + a);
        System.out.println("b = " + b);
        
        System.out.println(a + " + " + b + " = " + (a + b));
        
        System.out.println(a + " - " + b + " = " + (a - b));
        
        System.out.println(a + " * " + b + " = " + (a * b));
        
        System.out.println(a + " / " + b + " = " + (a / b));
        
        System.out.println(a + " % " + b + " = " + (a % b));
        
	}

}
